import React from 'react';
import Navbar from './Navbar';
import TaskCard from './TaskCard'


class Dashboard extends React.Component{

    constructor(props)
    {
        super(props);
        this.footerLast = this.footerLast.bind(this);
        this.render = this.render.bind(this);
        this.changeMode = this.changeMode.bind(this);
        this.state={mode: "mytasks", baseURL: this.props.location.state.baseURL , authToken : this.props.location.state.authToken , uid : this.props.location.state.uid};

    }

    changeMode(modeName)
    {
        var downIcons = document.getElementsByClassName("down-icon-items");
        if (this.state.mode == modeName )
            {
                return
            }
        for (var i=0;i<downIcons.length;i++)
            {
                downIcons[i].setAttribute("id","");
            }
        if ( modeName=="teams" )
        {
            downIcons[0].setAttribute("id","present-page");
            this.state.mode= modeName;
        }
        else if ( modeName=="mytasks" )
        {
            downIcons[1].setAttribute("id","present-page");
            this.state.mode= modeName;

        }
        else if ( modeName=="archives" )
        {
            downIcons[2].setAttribute("id","present-page");
            this.state.mode= modeName;
            this.render();

        }
        else{
            console.log("cannot set mode")
        }

    }

    footerLast() 
    {

         return(
        <footer className="down-nav">

        <div className="col-lg-5 col-lg-offset-1 col-md-12 down-icon-items" onClick={() => this.changeMode("teams")}><i className="fa fa-slideshare left-icons"></i>
            <p className="text-center">Teams </p>
        </div>
        <div className="col-lg-5 col-lg-offset-1 col-md-12 down-icon-items" id="present-page" onClick={() => this.changeMode("mytasks")} ><i className="fa fa-tasks left-icons"></i>
            <p className="text-center">My Tasks</p>
        </div>
        <div className="col-lg-5 col-lg-offset-1 col-md-12 down-icon-items" onClick={() => this.changeMode("archives")} ><i className="fa fa-history left-icons"></i>
            <p className="text-center">Archives </p>
        </div>

        </footer> 
         );


    }

    taskcards()
    {
        //const fetchurl=this.state.baseURL+"/user/"+this.state.uid+"/tasks/all?"+this.state.authToken ;
        const fetchurl = "https://task-line.herokuapp.com/user/rahul/tasks/all?key=eyJhbGciOiJIUzI1NiJ9.eyJpZCI6InJhaHVsIiwibmFtZSI6InJhaHVsIiwicm9sZSI6ImFkbWluIn0.tYUgIZXi8V4Twq_QdPC1rsfMltaqmCwZRUYhmp8hiEY"
        var responseCode = 0;
        var taskArray = [];
        fetch(fetchurl)
        .then(res => 
              { 
            responseCode = res.status;

                if (responseCode == 200 )
                    {
                        return res.json(); 
                    }
              })
        .then( data => 
              {
                    if ( data )
                        {
                            for( var i=0; i<data.length; i++)
                                {
                                    if (data[i]["progress"] == "to do")
                                        {
                                            var taskName = data[i]["name"];
                                            var taskReport = data[i]["report_to"];
                                            var taskPriority = data[i]["priority"];
                                            var taskDue = data[i]["due_date"];

                                            taskArray.push(
                                                <TaskCard key={i} name={taskName} reporter={taskReport} priority={taskPriority} due={taskDue} /> 
                                                          );
                                            alert("here");
                                            return (<TaskCard key={i} name={taskName} reporter={taskReport} priority={taskPriority} due={taskDue} /> );
                                            console.log(taskArray);

                                        }

                                }
                            return {taskArray}
                        }
                    else
                    {
                        alert("Cannot Fetch Data Try Later!!");
                    }
            });

        return(
        /*<div>
        <div className="col-lg-3 col-lg-offset-0 col-md-3 col-sm-5 col-xs-10" id="task-card">
            <h4 className="text-left team-card-title">Task A</h4>
            <p>To-Do : 11/12 </p>
            <span className="label label-primary">Priority </span>
            <p>To-DO</p>
            <span className="label label-warning">Deadline </span>
            <p>12/01/2020 </p>
            <span className="label label-primary">Current Progress </span>
            <div className="progress">
                <div className="progress-bar progress-bar-info" aria-valuenow="90" aria-valuemin="0" aria-valuemax="100" style={{width: "90%"}} >90%</div>
            </div>
        </div> */
            <div>
            {taskArray}
            </div>

        )
    }




    render()
    {

        return(
            <div style={{backgroundColor:"white"}}>
            <Navbar></Navbar>
            <div className="row" id="main-row">
                <div className="col-md-12">
                    <h3>My Tasks</h3>
                    <hr />
                </div>
            <div className="col-md-12">
                <h5 className="text-info">Pending </h5>
                <hr />
                <div className="row task-row">
                    <div className="col-md-12">
                        <div className="task-cards-container">
                        {this.taskcards()}
                        </div>
                    </div>
                </div>
            </div>
            </div>

                {this.footerLast()}
             </div>
        );
    }

}
export default Dashboard;
