import React from 'react';
import  { Redirect } from 'react-router-dom';

class Home extends React.Component{


    constructor(props) 
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = { uid : "0", authToken : "" };
    }



    handleSubmit(event) {
        event.preventDefault();

        const formdata = new FormData(event.target);

        const baseURL = "http://task-line.herokuapp.com";

        const sendData = new URLSearchParams({'username': formdata.get("username"),'password' : formdata.get("password")});

        var  responseCode = 404;
        var username = formdata.get("username");

        fetch(baseURL+"/login",{method: 'POST' ,body: sendData })
        .then(res => 
              { 
            responseCode = res.status;

            if ( responseCode == "200" & (res.done) )
            {
                alert(responseCode);
               return res.json() 
            }
              })
        .then( data => 
              {
                    if ( data )
                        {
                            var token = data["token"];
                            var userid = data["id"];
                            this.state.uid = userid;
                            this.state.authToken = token;
                            console.log(this.state.uid,this.state.authToken);

                        }





                /*

                    var presentURL = baseURL+"/user/"+username;
                    fetch(presentURL,{method:"GET"})
                    .then((response) => 
                          {
                        responseCode = response.status;
                        console.log(responseCode);
                        response.json();
                            })
                    .then(data => 
                          {
                    if (responseCode== 200)
                    {
                        alert(data);
                    }
                    else
                        {
                            alert("nodata");
                        }
               //this.props.history.push("/dashboard");
                    });
                }
                else
                {
                    document.getElementById("errorMessage").innerHTML="Invalid Credentials!!!";
                }*/
            });

    }


    render(){

         const mystyle = {
            marginTop:"30vh"
            };
        return(
            <div className="ui middle aligned center aligned grid login-component" style ={mystyle}>
            <div className="column" style={{color:"firebrick"}}>
            <h2 className="ui teal image header">
            <div className="content">
                LOGIN FOR <div className="extras">TASKS</div>
            </div>
            </h2>
            <form className="ui large form" onSubmit={this.handleSubmit} >
            <div className="ui stacked segment" style={{borderRadius:"3%",
              backgroundColor:"rgba(69, 62, 62, 0.5)"}}>
                <div className="field" >
                <div className="ui left icon input">
                <i className="user icon"></i>
                <input type="text" name="username" placeholder="Username "/>
                </div>
                </div>
                <div className="field ">
                <div className="ui left icon input">
                    <i className="lock icon"></i>
                    <input type="password" name="password" placeholder="Password"/>
                </div>
            </div>


            <button type="submit" className="ui fluid large submit button">LOGIN</button>
      </div>
    </form>

    <div id="errorMessage"  style={{color:"white"}}></div>
  </div>
</div>

        )

    }

}
export default Home;
