import React from 'react';
import Navbar from './Navbar';


class Dashboard extends React.Component{

    constructor(props)
    {
        super(props);
        this.footerLast = this.footerLast.bind(this);
        this.render = this.render.bind(this);
        this.changeMode = this.changeMode.bind(this);
    }

    changeMode(i)
    {
        var downIcons = document.getElementsByClassName("down-icon-items");

        if (this.state.uid =="mytasks")
        {
            downIcons[1].id ="present-page";

        }
        else if (this.state.mode=="archives") 
        {
            downIcons[2].id = "present-page";

        }
        else 
        {
            downIcons[0].id = "present-page";    

        }
    }

    footerLast() 
    {

         return(
        <footer className="down-nav">

        <div className="col-lg-5 col-lg-offset-1 col-md-12 down-icon-items" onClick={this.changeMode(0)}><i className="fa fa-slideshare left-icons"></i>
            <p className="text-center">Teams </p>
        </div>
        <div className="col-lg-5 col-lg-offset-1 col-md-12 down-icon-items" onClick={this.changeMode(1)}><i className="fa fa-tasks left-icons"></i>
            <p className="text-center">My Tasks</p>
        </div>
        <div className="col-lg-5 col-lg-offset-1 col-md-12 down-icon-items" onClick={this.changeMode(2)}><i className="fa fa-history left-icons"></i>
            <p className="text-center">Archives </p>
        </div>
    </footer> 
         );


        }


    render()
    {
        return(
            <div style={{backgroundColor:"white"}}>
            <Navbar></Navbar>
            {this.footerLast()}
             </div>
        );
    }

}
export default Dashboard;
