import React from 'react';
import  { Redirect } from 'react-router-dom';
import DashBoard from './Dashboard';

class Home extends React.Component{


    constructor(props) {
    super(props);
    this.Home = this.Home.bind(this);
        console.log(this.props)
    }



    handleSubmit(event) {
        event.preventDefault();
        //console.log({this.props});
        const formdata = new FormData(event.target);

        const url = "https://rocky-temple-63907.herokuapp.com/login";

        const sendData = new URLSearchParams({'username': formdata.get("id"),'password' : formdata.get("password")});
        var  responseCode = 404;
        fetch(url,{method: 'POST' ,body: sendData})
        .then((response) => {responseCode = response.status})
        .then(data => {
            console.log(responseCode);
            if (responseCode == 200){
               //this.props.history.push("/dashboard");
            }
            else{
                document.getElementById("errorMessage").innerHTML="Invalid Credentials!!!";
            }
        });

    }


    render(){

         const mystyle = {
            marginTop:"30vh"
            };
        return(
            <div className="ui middle aligned center aligned grid login-component" style ={mystyle}>
            <div className="column" style={{color:"firebrick"}}>
            <h2 className="ui teal image header">
            <div className="content">
                LOGIN FOR <div className="extras">TASKS</div>
            </div>
            </h2>
            <form className="ui large form" onSubmit={this.handleSubmit} >
            <div className="ui stacked segment" style={{borderRadius:"3%",
              backgroundColor:"rgba(69, 62, 62, 0.5)"}}>
                <div className="field" >
                <div className="ui left icon input">
                <i className="user icon"></i>
                <input type="text" name="id" placeholder="user/employee id "/>
                </div>
                </div>
                <div className="field ">
                <div className="ui left icon input">
                    <i className="lock icon"></i>
                    <input type="password" name="password" placeholder="Password"/>
                </div>
            </div>


            <button type="submit" className="ui fluid large submit button">LOGIN</button>
      </div>
    </form>

    <div id="errorMessage"  style={{color:"white"}}></div>
  </div>
</div>

        )

    }

}
export default Home;
