import React from 'react';


class Home extends React.Component{


    handleSubmit(event) {
        event.preventDefault();
        const formdata = new FormData(event.target);
        //const url="https://rocky-temple-63907.herokuapp.com/login?id="+formdata.get("id")+"&password="+formdata.get("password");
        const url = "https://rocky-temple-63907.herokuapp.com/login";
        const sendData = new URLSearchParams({'id': formdata.get("id"),'password' : formdata.get("password")});
        fetch(url,{method: 'POST' ,body: sendData})
        .then((response) => {console.log(response.status)}).then(data => {console.log(data)});
    }


    render(){

         const mystyle = {
            marginTop:"30vh"
            };
        return(

            <div className="ui middle aligned center aligned grid login-component" style={mystyle}>
  <div className="column" style={{color:"firebrick"}}>
    <h2 className="ui teal image header">
      <div className="content">
          LOGIN FOR <div className="extras">TASKS</div>
      </div>
    </h2>
    <form className="ui large form" onSubmit={this.handleSubmit} >
      <div className="ui stacked segment" style={{borderRadius:"3%",
              backgroundColor:"rgba(69, 62, 62, 0.5)"}}>
        <div className="field" >
          <div className="ui left icon input">
            <i className="user icon"></i>
            <input type="text" name="id" placeholder="user/employee id "/>
          </div>
        </div>
        <div className="field ">
          <div className="ui left icon input">
            <i className="lock icon"></i>
            <input type="password" name="password" placeholder="Password"/>
          </div>
        </div>
 <div className="ui fluid large submit button" style={{backgroundColor:"lightblue"}}>LOGIN</div>

            <button type="submit" className="ui fluid large submit button">LOGIN</button>
      </div>

      <div className="ui error message"></div>

    </form>
  </div>
</div>

        )

    }

}
export default Home;
