import React from 'react';



class TaskCard extends React.Component{

     constructor(props) 
    {
        super(props);
        this.state= {deadline : new Date(this.props.due)}

    }

    componentDidMount()
    {



    }

    render(){

        return(
            <div className="col-lg-3 col-lg-offset-0 col-md-3 col-sm-5 col-xs-10" id="task-card">
            <h4 className="text-left team-card-title">{this.props.name}</h4>
            <p>Report : {this.props.reporter} </p>
            <span className="label label-primary">Priority </span>
            <p>{this.props.priority}</p>
            <span className="label label-warning">Deadline </span>
            <p>{this.state.deadline.toString()} </p>
            <span class="label label-primary">Current Progress </span>
            <div className="progress">
                <div className="progress-bar progress-bar-info" aria-valuenow="{this.props.pending}" aria-valuemin="0" aria-valuemax="{this.props.pending}" style={{width: "90%"}}>90%</div>
            </div>
            </div>
        )

    }

}

export default TaskCard;
